import React, {Component} from 'react';
import {Bar} from 'react-chartjs-2';

class Chart extends Component{

    constructor(props){
        super(props);
        this.state = {
            chartData:{
                labels: ['this', 'is', 'a', 'test'],
                datasets: [
                    {
                        label: 'Test label',
                        data:[1, 5, 3, 8],
                        backgroundColour:['rgba(255, 99, 132, 0)', 'rgba(54, 162, 235, 0)', 'rgba(255, 99, 132, 0.6)', 'rgba(54, 162, 235, 0.6)']
                    }
                ]
            }
        }
    }

    render(){
        return(
            <div className="chart">
                <Bar
                data={this.state.chartData}
                options={{
                    maintainAspectRatio: false
                }}
                />
            </div>
        )
    }
}

export default Chart;
