import React, {Component} from 'react';
import {Modal, Button, Form} from 'react-bootstrap';


class AddRunnerComponent extends Component{
    constructor(props){
        super(props);

        this.state = {
            firstName: '',
            lastName: '',
            SIID: ''
        };

        this.onChangeFirstName = this.onChangeFirstName.bind(this);
        this.onChangeLastName = this.onChangeLastName.bind(this);
        this.onChangeSIID = this.onChangeSIID.bind(this);

        this.onHide = this.props.onHide.bind(this);

        this.onCloseModal = this.onCloseModal.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

    }


    componentDidMount() {
        this.setState({
            firstName: '',
            lastName: '',
            SIID: ''
        })
    }

    onChangeFirstName(e){
        this.setState({
            firstName: e.target.value
        });
    }

    onChangeLastName(e){
        this.setState({
            lastName: e.target.value
        });
    }

    onChangeSIID(e){
        this.setState({
            SIID: e.target.value
        });
    }


    onSubmit(e){
        e.preventDefault();

        const runner = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            SIID: this.state.SIID
        }

        console.log(runner);
    }

    onCloseModal(e){
        e.preventDefault();
        this.setState({
            firstName: '',
            lastName: '',
            SIID: '',
        });

    }


    render(){
        return(


            <Modal
             {...this.props}
             size="med"
             aria-labelledby="contained-modal-title-vcenter"
             centered
             >

             <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Add a new runner
                 </Modal.Title>
             </Modal.Header>
                <Modal.Body>

                   <Form>
                        <Form.Group controlId="newRunnerFirstName">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control type="text" value={this.state.firstName} onChange={this.onChangeFirstName} placeholder="Enter First Name" />
                        </Form.Group>
                        <Form.Group controlId="newRunnerLastName">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control type="text" value={this.state.lastName} onChange={this.onChangeLastName} placeholder="Enter Last Name" />
                        </Form.Group>
                        <Form.Group controlId="newRunnerSIID">
                            <Form.Label>SIID</Form.Label>
                            <Form.Control type="text" value={this.state.SIID} onChange={this.onChangeSIID} placeholder="Enter SIID" />
                        </Form.Group>
                   </Form>

               </Modal.Body>
             <Modal.Footer>

                <Button variant="danger" onClick={this.onCloseModal}>Close</Button>
                <Button variant="primary" onClick={this.onSubmit}>Save</Button>

             </Modal.Footer>
           </Modal>
        )
    }
}

export default AddRunnerComponent;
